// src/components/MapComponent.jsx
import React, { useEffect, useRef } from 'react';
import L from 'leaflet';
import 'leaflet/dist/leaflet.css';

const MapComponent = ({ destination, locations }) => {
  const mapRef = useRef(null);

  useEffect(() => {
    if (locations.length > 0) {
      const firstLoc = locations[0];
      const [lat, lng] = firstLoc.coordinates.split(',').map(Number);

      // Initialize map
      const map = L.map(mapRef.current).setView([lat, lng], 13);

      // Add tile layer
      L.tileLayer('https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png', {
        attribution: '© OpenStreetMap contributors'
      }).addTo(map);

      // Add markers
      locations.forEach(loc => {
        const [markerLat, markerLng] = loc.coordinates.split(',').map(Number);
        L.marker([markerLat, markerLng])
          .addTo(map)
          .bindPopup(loc.location)
          .bindTooltip(loc.activity || loc.location);
      });

      // Cleanup
      return () => {
        map.remove();
      };
    }
  }, [locations]);

  return (
    <div 
      ref={mapRef} 
      style={{ 
        height: '500px', 
        width: '100%',
        borderRadius: '8px',
        overflow: 'hidden',
        marginTop: '16px'
      }}
    />
  );
};

export default MapComponent;
