import React, { useState } from 'react';
import { Button, Col, Row, Image, Typography } from 'antd';

const { Title, Text } = Typography;

const Homepage = () => {
  const [user, setUser] = useState(null);

  const handleLogin = () => {
    // Navigate to auth page
    window.location.href = '/auth';
  };

  const handleSignUp = () => {
    // Navigate to auth page
    window.location.href = '/auth';
  };

  const handleStartPlanning = () => {
    // Navigate to chatbot page
    window.location.href = '/chatbot';
  };

  return (
    <div style={{ padding: '24px' }}>
      <Title level={1}>‚úàÔ∏è AI Travel Planner</Title>
      
      <Row gutter={16}>
        <Col span={16}>
          <Title level={3}>Plan Your Perfect Trip with AI</Title>
          <Text>
            Let our AI-powered travel planner help you create the perfect itinerary!
          </Text>
          <ul>
            <li>ü§ñ Chat with our AI to plan your trip</li>
            <li>üó∫Ô∏è Get personalized recommendations</li>
            <li>üå§Ô∏è Check weather forecasts</li>
            <li>üìç Explore interactive maps</li>
          </ul>
          
          {!user ? (
            <Row gutter={16}>
              <Col span={12}>
                <Button type="primary" block onClick={handleLogin}>
                  Login
                </Button>
              </Col>
              <Col span={12}>
                <Button type="primary" block onClick={handleSignUp}>
                  Sign Up
                </Button>
              </Col>
            </Row>
          ) : (
            <Button type="primary" block onClick={handleStartPlanning}>
              Start Planning
            </Button>
          )}
        </Col>
        
        <Col span={8}>
          <Image
            src="https://your-travel-image-url.jpg"
            alt="Travel"
            style={{ width: '100%' }}
          />
        </Col>
      </Row>
    </div>
  );
};

export default Homepage;
