// src/components/AuthPage.jsx
import React, { useState } from 'react';
import { Tabs, Input, Button, message } from 'antd';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

const { TabPane } = Tabs;

const AuthPage = () => {
  const [activeTab, setActiveTab] = useState('login');
  const [loginEmail, setLoginEmail] = useState('');
  const [loginPassword, setLoginPassword] = useState('');
  const [signupEmail, setSignupEmail] = useState('');
  const [signupPassword, setSignupPassword] = useState('');
  const [confirmPassword, setConfirmPassword] = useState('');
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();

  const API_URL = 'http://localhost:8000/api';
 // Hardcoded credentials
 const hardcodedEmail = "admin@example.com";
 const hardcodedPassword = "password123";
  const handleLogin = async () => {
    try {
      setLoading(true);
      const response = await axios.post(`${API_URL}/auth/login`, {
        email: loginEmail,
        password: loginPassword
      });

      if (response.data.success) {
        message.success('Logged in successfully!');
        localStorage.setItem('user', JSON.stringify({ email: response.data.email }));
        navigate('/home');
      }
    } catch (error) {
      message.error('Invalid credentials');
      console.error('Login error:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleSignup = async () => {
    if (signupPassword !== confirmPassword) {
      message.error("Passwords don't match");
      return;
    }

    try {
      setLoading(true);
      const response = await axios.post(`${API_URL}/auth/signup`, {
        email: signupEmail,
        password: signupPassword
      });

      if (response.data.success) {
        message.success('Account created successfully!');
        localStorage.setItem('user', JSON.stringify({ email: response.data.email }));
        navigate('/home');
      }
    } catch (error) {
      message.error(error.response?.data?.detail || 'Error creating account');
      console.error('Signup error:', error);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="auth-container">
      <Tabs activeKey={activeTab} onChange={setActiveTab} centered>
        <TabPane tab="Login" key="login">
          <div className="auth-form">
            <Input
              placeholder="Email"
              value={loginEmail}
              onChange={(e) => setLoginEmail(e.target.value)}
              style={{ marginBottom: 16 }}
            />
            <Input.Password
              placeholder="Password"
              value={loginPassword}
              onChange={(e) => setLoginPassword(e.target.value)}
              style={{ marginBottom: 16 }}
            />
            <Button type="primary" block onClick={handleLogin} loading={loading}>
              Login
            </Button>
          </div>
        </TabPane>

        <TabPane tab="Sign Up" key="signup">
          <div className="auth-form">
            <Input
              placeholder="Email"
              value={signupEmail}
              onChange={(e) => setSignupEmail(e.target.value)}
              style={{ marginBottom: 16 }}
            />
            <Input.Password
              placeholder="Password"
              value={signupPassword}
              onChange={(e) => setSignupPassword(e.target.value)}
              style={{ marginBottom: 16 }}
            />
            <Input.Password
              placeholder="Confirm Password"
              value={confirmPassword}
              onChange={(e) => setConfirmPassword(e.target.value)}
              style={{ marginBottom: 16 }}
            />
            <Button type="primary" block onClick={handleSignup} loading={loading}>
              Sign Up
            </Button>
          </div>
        </TabPane>
      </Tabs>

      <style jsx>{`
        .auth-container {
          max-width: 400px;
          margin: 0 auto;
          padding: 24px;
        }
        .auth-form {
          padding: 24px;
        }
      `}</style>
    </div>
  );
};

export default AuthPage;
