// src/App.js
import React from 'react';
import { Routes, Route, Navigate } from 'react-router-dom';
import { Layout } from 'antd';
import Homepage from './Homepage';
import Chatbot from './Chatbot';
import AuthPage from './AuthPage';
import './App.css';

const { Header, Content } = Layout;

// Mock authentication function
const isAuthenticated = () => {
  // Check if user is logged in (this could be based on local storage, context, etc.)
  return localStorage.getItem('user') !== null; // Adjust this logic as needed
};

const App = () => {
  return (
    <Layout className="app-layout">
      <Header className="app-header">
        <h1>✈️ ChaloChalein</h1>
      </Header>
      <Content className="app-content">
        <Routes>
          <Route path="/" element={<Homepage />} />
          <Route path="/home" element={<Homepage />} />
          <Route path="/auth" element={<AuthPage />} />
          <Route path="/chatbot" element={isAuthenticated() ? <Chatbot /> : <Navigate to="/auth" />} />
        </Routes>
      </Content>
    </Layout>
  );
};

export default App;
